import React, { Component } from 'react';
import TheatreService from '../../services/TheatreService';
// import './Booking.css';


class Booking extends Component {

    constructor(props) {
        super(props)
        this.state={
            custid:this.props.match.params.cid,
            showid:this.props.match.params.shid,

            transactionmode:'',
            bookingdate:''
        
        }

    }

    insertBooking=(b)=>{
        b.preventDefault();
        let booking={bookingDate:this.state.bookingate,
                     theatreCity:this.state.theatre_city,
                     managerName:this.state.manager_name,
                     managerContact:this.state.manager_contact}
                     console.log((JSON.stringify(theatre)));
                     TheatreService.theatreAdd(theatre)
                     .then(response => {
                        console.log('Theatre Added successfully', response.data);
                        this.props.history.push('/TheatreList');
                    });
    }





    render() {
        return (
            <section id="booking">
            <div class="custom-container">

                <form >
                <label for="">Transaction Mode</label>
                    <div class="form-coloumn">
                        <div class="form-group">
                            <div class="input-data">
                            
                                <input
                                    type="text"
                                    class="form-control"
                                    formControlName="transactionMode"
                                    required
                                />
                                <div class="underline"></div>
                                
                                <input
                                    type="radio"
                                    id="rad1"
                                    value="admin"
                                    formControlName="transactionMode"
                                />
                                <label for="rad1">Online</label>
                                <input
                                    type="radio"
                                    id="rad2"
                                    formControlName="transactionMode"
                                    value="offline"
                                />
                                <label for="rad2">Offline</label>
                            </div>
                        </div>
                    </div>
                    <br></br>
                    <div class="form-group">
                        <div class="form-row">
                            <div class="date">
                                <input
                                    type="date"
                                    class="form-control"
                                    formControlName="bookingDate"
                                />
                            </div>
                        </div>
                    </div>
                    <div>
                        <button type="submit" class="btn btn-danger">
                            Cancel
                        </button>
                        <button type="submit" class="btn btn-primary">
                            Proceed <i class="icofont-long-arrow-right"></i>
                        </button>
                    </div>
                </form>
            </div >
             </section >




        );
    }
}


export default Booking;
