import React, { Component } from 'react';
// import UpdateCustomer from './UpdateCustomer';
import { Navbar, Nav, Container } from 'react-bootstrap';
// import ShowService from '../../services/ShowService';
import TheatreService from '../../services/TheatreService';

class ViewShow extends Component {

    constructor(props) {
        super(props)
        this.state = {
            op: this.props.match.params.id,
            theatrearr: [],
            moviearr: [],
            screenarr: [],
            showarr: [],
            userid: sessionStorage.getItem("uid"),
            username: sessionStorage.getItem("name"),
            password: sessionStorage.getItem("password"),
            role: sessionStorage.getItem("role"),
            custid: sessionStorage.getItem("custId"),

        }
    }

    componentDidMount() {
        console.log("in Availabel Shows componentDidMount");




        // var data = sessionStorage.getItem("name");
        // console.log("Name: " + data);


        TheatreService.getTheatreByMovieId(this.state.op).then((response) => {
            let theatreobj = response.data;


            this.setState({
                theatrearr: theatreobj
                // screenarr: theatreobj.screen, showarr: theatreobj.screen.show, moviearr: theatreobj.screen.show.movie
            })
            console.log(this.state.theatrearr)
            console.log(this.state.screenarr)

        })

    }

    //   bookMovie = (movieId) => {
    //     console.log("in movie edit ")
    //     sessionStorage.setItem("mid", movieId)
    //     this.props.history.push(`/Customer/BookMovie/${movieId}`)
    //   }

    //   deleteMovie = (movieId) => {
    //     console.log("in delete movie");
    //     MovieService.deleteMovie(movieId).then((response) => {
    //       this.props.history.push('Admin')
    //       console.log("deleted succesfully")
    //     })
    //   }

    // showList = () => {
    //     console.log("in show list movie");
    //     this.props.history.push(`/Customer/AvailableShows`)
    // }

    render() {
        return (

            <div className='container'>
            <h1> Screen Details</h1>
            <hr/>
            <div>
                <table className='table table-bordered table-striped'>
                    <thead className="thead-dark">
                        <tr>
                            <th>TheatreId</th>
                            <th>TheatreName</th>
                            <th>theatreCity</th>
                             
                        </tr>


                    </thead>
                    <tbody>
                        {
                            <tr >
                                <td>{this.state.theatreob.theatreId}</td>
                                <td>{this.state.theatreob.theatreName}</td>
                                <td>{this.state.theatreob.theatreCity}</td>
                                
                                
                                
                                
                            </tr>
                        }
                    </tbody>
                    

                </table>
            </div>
            <div>
            <table className='table table-bordered table-striped'>
                    <thead className="thead-dark">
                        <tr>
                            <th>ScreenId</th>
                            <th>ScreenName</th>
                            <th>rows</th>
                            <th>columns</th>
                             
                        </tr>


                    </thead>
                    <tbody>
                        {
                            this.state.screenarr.map((screen)=>
                            <tr key={screen.screenId}>
                                <td>{screen.screenId}</td>
                                <td>{screen.screenName}</td>
                                <td>{screen.rows}</td>
                                <td>{screen.columns}</td>
                                <td>{this.state.showarr.map((screen))}</td>
                                <td> <button className="btn btn-danger" onClick={()=>this.addshow(this.state.theatreob.theatreId,screen.screenId)}>add show</button></td>

                            </tr>)
                            
                                
                                
                                
                                
                                
                        
                        }
                    </tbody>
                    

                </table>
            </div>
        </div>

        );
    }
}

export default ViewShow;
