import React, { Component } from 'react';
// import UpdateCustomer from './UpdateCustomer';
import { Navbar, Nav, Container } from 'react-bootstrap';
// import ShowService from '../../services/ShowService';
import TheatreService from '../../services/TheatreService';

class Customer extends Component {

    constructor(props) {
        super(props)
        this.state = {
            showarr: [],
            // moviearr:[],
            // theatrearr:[],
            userid: sessionStorage.getItem("uid"),
            username: sessionStorage.getItem("name"),
            password: sessionStorage.getItem("password"),
            role: sessionStorage.getItem("role"),
            custid: sessionStorage.getItem("custId"),

        }
    }

    componentDidMount() {
        console.log("in Availabel Shows componentDidMount");



        console.log(sessionStorage.getItem("name"));
        console.log(sessionStorage.getItem("password"));
        console.log(sessionStorage.getItem("role"));

        // var data = sessionStorage.getItem("name");
        // console.log("Name: " + data);


        TheatreService.getAllTheatres().then((response) => {
            this.setState({ showarr: response.data })
            console.log(this.state.showarr)

        })
    }

    //   bookMovie = (movieId) => {
    //     console.log("in movie edit ")
    //     sessionStorage.setItem("mid", movieId)
    //     this.props.history.push(`/Customer/BookMovie/${movieId}`)
    //   }

    //   deleteMovie = (movieId) => {
    //     console.log("in delete movie");
    //     MovieService.deleteMovie(movieId).then((response) => {
    //       this.props.history.push('Admin')
    //       console.log("deleted succesfully")
    //     })
    //   }

    // showList = () => {
    //     console.log("in show list movie");
    //     this.props.history.push(`/Customer/AvailableShows`)
    // }

    render() {
        return (
            <div>
                <h1>welcome {this.state.username}</h1>
                <div>
          <Navbar bg="dark" variant="dark">
            <Container>

              <Nav className="me-auto">
                <Nav.Link href="./Home">Home</Nav.Link>
                <Nav.Link href="./Movies">Movies</Nav.Link>
              </Nav>
              <Nav className="me-auto ">

              </Nav>
              <Nav className=" text-right">
                <Nav.Link href="./UpdateCustomer">Update Details</Nav.Link>

                <Nav.Link href="./Logout">SignOut</Nav.Link>
              </Nav>

            </Container>
          </Navbar>
        </div>

                <h2 className="text-center">Available Shows</h2>

                <div className='row'>
                    <table striped bordered hover variant="dark">
                        <thead>
                            <tr>
                                
                                <th> Movie Name</th>
                                <th> Theatre Name</th>
                                <th> City</th>
                                <th> Screen Name</th>
                                <th> Show Start Time</th>
                                <th> Show End Time</th>

                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.showarr.map((ListedShow) =>
                                    <tr key={ListedShow.theatre.theatreId}>
                                        <td>{ListedShow.theatre.screen.show.movie.movieName}</td>
                                        <td>{ListedShow.theatre.theatreName}</td>
                                        <td>{ListedShow.theatre.theatreCity}</td>
                                        <td>{ListedShow.theatre.screen.screenName}</td>
                                        <td>{movie.movieRating}</td>
                                        <td>
                      <button className="btn btn-success" onClick={() => this.bookMovie(movie.movieId)}>Book Now</button> </td>
                    <td>  <button className="btn btn-danger" onClick={() => this.deleteMovie(movie.movieId)}>delete</button></td>
                    <td> <button className="btn btn-success">view</button>
                    </td>
                                    </tr>
                                )
                            }
                        </tbody>

                    </table>
                    <div className="text-center">
                        <button className="btn btn-success" onClick={() => this.bookMovie(movie.movieId)}>Book Now</button> 
                        <button className="btn btn-success" onClick={() => this.showList()} > Available Shows</button>
                    </div>
                </div>

            </div>
        );
    }
}

export default Customer;
