import axios from 'axios';

const USER_API_BASE_URL="http://localhost:8000/theatre";

class TheatreService{

    
    theatreAdd(theatre){
        return axios.post(USER_API_BASE_URL+'/insert',theatre,{
        headers:{
            'Content-Type':'application/json'
        }
        });
    }
    getAllTheatres(){
        return axios.get(USER_API_BASE_URL+'/all')
    }


    getTheatreByMovieId(id){
        return axios.get(USER_API_BASE_URL+'/findby')
    }

    getByTheatreId(id){
        return axios.get(USER_API_BASE_URL+'/find/'+id)
    }
    editTheatre(theatre){
        return axios.put(USER_API_BASE_URL+'/update',theatre)
    }
    deleteTheatre(theatreId){
        return axios.delete(USER_API_BASE_URL+'/delete/'+theatreId)
    }

    
    
}
export default new TheatreService();
