import React, { Component } from 'react';
import BookingService from '../../services/BookingService';
// import './Booking.css';


class Booking extends Component {

    constructor(props) {
        super(props)
        this.state={
            custid:this.props.match.params.cid,
            showid:this.props.match.params.shid,
            
            transactionmode:'',
            bookingdate:''
        
        }

    }

    insertBooking=(b)=>{
        b.preventDefault();
        let booking={bookingDate:this.state.bookingdate,
                     transactionMode:this.state.transactionmode
                    }
                     console.log((JSON.stringify(booking)));
                     BookingService.addBooking(booking,this.state.custid,this.state.showid)
                     .then(response => {
                        console.log('Booking Insert Successfully', response.data);
                        this.props.history.push('/seat');
                    });
    }





    render() {
        return (
            <section id="booking">
            <div class="custom-container">

                <form >
                <label for="">Transaction Mode</label>
                <div class="radio">
            <input
              type="radio"
              id="admin"
              name="role"
              value="admin"
              formControlName="role"
              class="radio"
              value="admin"
              checked={this.state.role === "admin"}
              onChange={this.onChangeValue}
            />
            <label class="radioName" for="male">Admin</label><br />
            <input
              type="radio"
              id="customer"
              name="role"
              value="customer"
              formControlName="role"
              class="radio"
              value="customer"
              checked={this.state.role === "customer"}
              onChange={this.onChangeValue}
            />
            <label class="radioName" for="female">Customer</label><br />
          </div>
                    

                    <br></br>


                    <div class="form-group">
                        <div class="form-row">
                            <div class="date">
                                <input
                                    type="date"
                                    class="form-control"
                                    formControlName="bookingDate"
                                />
                            </div>
                        </div>
                    </div>


                    <div>
                        <button type="submit" class="btn btn-danger">
                            Cancel
                        </button>
                        <button type="submit" class="btn btn-primary">
                            Proceed <i class="icofont-long-arrow-right"></i>
                        </button>
                    </div>
                </form>
            </div >
             </section >




        );
    }
}


export default Booking;
