import React, { Component } from 'react';
import axios from 'axios';

class ViewBooking extends Component {
    constructor(props) {
        super(props);
        this.state = {
            op: this.props.match.params.bid,
            bookingOb: [],
            transactionMode: '',
            bookingDate: '',
            screenName: '',
            showName: '',
            showDate: '',
            showStartTime: '',
            showEndTime: '',
            movieName: '',
            movieHours: '',
            showId: '',
            customerId: sessionStorage.getItem("custId")

        }

    }
    componentDidMount() {
        console.log("in component didmount of view movie");
        console.log(this.state.customerId);
        axios.get(`http://localhost:8000/booking/viewbooking/${this.state.op}`)
            .then((response) => {
                let booking = response.data;
                
                this.setState({ bookingOb: booking });
                console.log(this.state.bookingOb);
                console.log("printing Booking");
                this.state.bookingOb.map((booking) => {
                    this.setState({ bookingDate: booking.bookingDate });
                    this.setState({ transactionMode: booking.transactionMode });

                    console.log(booking.transactionId, booking.bookingDate, booking.transactionMode);

                    booking.show.map((show1) => {
                        this.setState({ showDate: show1.showDate });
                        this.setState({ showStartTime: show1.showStartTime });
                        console.log(show1.showId, show1.showStartTime,show1.showEndTime, show1.showName ,show1.showDate);
                        
                        screen1.show.map((show1) => {
                            this.setState({ movieName: show1.movie.movieName });
                            this.setState({ movieHours: show1.movie.movieHours });
                            this.setState({ showName: show1.showName });
                            this.setState({ showDate: show1.showDate });
                            this.setState({ showStartTime: show1.showStartTime });
                            this.setState({ showEndTime: show1.showEndTime });
                            this.setState({ showId: show1.showId });

                            console.log(show1.showName, show1.showDate)
                            console.log(show1.movie.movieId)

                        })

                    })


                })




            })
    }


    bookShow = (customerId, showId) => {
        console.log("in Book Show ")
        // sessionStorage.setItem("mid", movieId)
        this.props.history.push(`/Customer/Booking/${customerId}/${showId}`)
      }


    render() {
        return (
            <div>
                <div>
                    <table className='table table-bordered table-striped'>
                        <thead className="thead-dark">
                            <tr>

                                <th>Movie</th>
                                <th>Show Date</th>
                                <th>ScreenName</th>
                                <th>Show Start Time</th>
                                <th>Transaction Mode</th>
                                <th>Transaction Status</th>


                            </tr>


                        </thead>
                        <tbody>
                            {
                                <tr >
                                    <td>{this.state.theatreName}</td>
                                    <td>{this.state.theatreCity}</td>
                                    <td>{this.state.screenName}</td>







                                </tr>
                            }
                        </tbody>


                    </table>
                </div>
                <div>
                    <table className='table table-bordered table-striped'>
                        <thead className="thead-dark">
                            <tr>

                                <th>MovieName</th>
                                <th>MovieHours</th>
                                <th>ShowId</th>
                                <th>ShowName</th>
                                <th>ShowDate</th>
                                <th>Show StartTime</th>
                                <th>Show EndTime</th>



                            </tr>


                        </thead>
                        <tbody>
                            {
                                <tr >
                                    <td>{this.state.movieName}</td>
                                    <td>{this.state.movieHours}</td>
                                    <td>{this.state.showId}</td>
                                    <td>{this.state.showName}</td>
                                    <td>{this.state.showDate}</td>
                                    <td>{this.state.showStartTime}</td>
                                    <td>{this.state.showEndTime}</td>








                                </tr>
                            }
                        </tbody>


                    </table>
                    <div className="text-center">
                        <button className="btn btn-success" onClick={() => this.bookShow(this.state.customerId, this.state.showId)}>Book Now</button>
                    </div>
                </div>
            </div>
        );
    }
}
// 

export default ViewShow;
